import React, { useState, useEffect } from 'react';
import { Card, Button, Input, Spinner } from '../components/common';
import * as mockApi from '../api/mock';
import { SaveIcon } from '../components/icons';

const SettingsPage = () => {
  const [companyInfo, setCompanyInfo] = useState(null);
  const [themeSettings, setThemeSettings] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSettings = async () => {
      try {
        setLoading(true);
        setError(null);
        const [infoData, themeData] = await Promise.all([
          mockApi.getCompanyInfo(),
          mockApi.getThemeSettings()
        ]);
        setCompanyInfo(infoData);
        setThemeSettings(themeData);
      } catch (e) {
        setError("Falha ao carregar as configurações.");
        console.error(e);
      } finally {
        setLoading(false);
      }
    };
    fetchSettings();
  }, []);

  const applyTheme = (settings) => {
    const root = document.documentElement;
    root.classList.remove('theme-petroleo', 'theme-roxo', 'theme-laranja');
    root.classList.add(`theme-${settings.primaryColor}`);
    root.classList.remove('font-size-sm', 'font-size-base', 'font-size-lg');
    root.classList.add(`font-size-${settings.fontSize}`);
  };

  const handleThemeChange = (key, value) => {
    if (!themeSettings) return;
    const newSettings = { ...themeSettings, [key]: value };
    setThemeSettings(newSettings);
    applyTheme(newSettings);
  };

  const handleInfoChange = (e) => {
    if (!companyInfo) return;
    const { name, value } = e.target;
    setCompanyInfo({ ...companyInfo, [name]: value });
  };

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0] && companyInfo) {
      const file = e.target.files[0];
      if (file.size > 1024 * 1024) {
        alert("O arquivo é muito grande. O limite é de 1MB.");
        return;
      }
      const reader = new FileReader();
      reader.onloadend = () => {
        setCompanyInfo({ ...companyInfo, logoBase64: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = async (e) => {
    e.preventDefault();
    if (!companyInfo || !themeSettings) return;
    setIsSaving(true);
    try {
      await Promise.all([
        mockApi.saveCompanyInfo(companyInfo),
        mockApi.saveThemeSettings(themeSettings)
      ]);
      alert("Configurações salvas com sucesso!");
    } catch (err) {
      alert("Falha ao salvar as configurações.");
      console.error(err);
    } finally {
      setIsSaving(false);
    }
  };

  if (loading) return <div className="flex justify-center p-12"><Spinner /></div>;
  if (error) return <div className="text-center text-danger p-12">{error}</div>;
  if (!companyInfo || !themeSettings) return <div className="text-center text-base-300 p-12">Não foi possível carregar as configurações.</div>;

  const colorOptions = [
    { key: 'petroleo', name: 'Petróleo', className: 'bg-[#2C7A7B]' },
    { key: 'laranja', name: 'Laranja', className: 'bg-[#C05621]' },
    { key: 'roxo', name: 'Roxo', className: 'bg-[#5A67D8]' },
  ];

  const fontOptions = [
    { key: 'sm', name: 'Pequeno' },
    { key: 'base', name: 'Médio' },
    { key: 'lg', name: 'Grande' },
  ];

  return (
    <form onSubmit={handleSave} className="space-y-6">
      <div className="flex justify-between items-start">
        <h1 className="text-3xl font-bold text-base-400">Configurações</h1>
        <button
          type="submit"
          disabled={isSaving}
          style={{
            backgroundColor: 'var(--main-color)',
            color: 'white',
            padding: '10px 16px',
            borderRadius: '6px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}
        >
          <SaveIcon />
          {isSaving ? 'Salvando...' : 'Salvar Todas as Alterações'}
        </button>
      </div>

      <Card>
        {/* ...Informações da Empresa (sem alterações de lógica)... */}
        {companyInfo.logoBase64 && (
          <button
            type="button"
            onClick={() => setCompanyInfo({ ...companyInfo, logoBase64: '' })}
            style={{
              backgroundColor: 'var(--main-color)',
              color: 'white',
              padding: '10px 16px',
              borderRadius: '6px',
            }}
          >
            Remover Logo
          </button>
        )}
      </Card>

      <Card>
        <h2 className="text-xl font-bold text-base-400 mb-4">Aparência do Sistema</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div>
            <h3 className="text-lg font-semibold text-base-300 mb-3">Cor Principal</h3>
            <div className="flex flex-wrap gap-4">
              {colorOptions.map(opt => (
                <button
                  key={opt.key}
                  type="button"
                  onClick={() => handleThemeChange('primaryColor', opt.key)}
                  className={`p-2 rounded-lg border-2 ${themeSettings.primaryColor === opt.key ? 'border-primary-600 ring-2 ring-primary-500' : 'border-transparent'}`}
                  style={{ color: 'white' }}
                >
                  <div className="flex items-center gap-3">
                    <span className={`block w-8 h-8 rounded-md ${opt.className}`}></span>
                    <span className="font-semibold text-base-400">{opt.name}</span>
                  </div>
                </button>
              ))}
            </div>
          </div>

          <div>
            <h3 className="text-lg font-semibold text-base-300 mb-3">Tamanho da Fonte</h3>
            <div className="flex flex-wrap items-center gap-2" role="group">
              {fontOptions.map(opt => (
                <button
                  key={opt.key}
                  type="button"
                  onClick={() => handleThemeChange('fontSize', opt.key)}
                  style={{
                    backgroundColor: themeSettings.fontSize === opt.key ? 'var(--main-color)' : 'var(--color-base-300)',
                    color: 'white',
                    padding: '8px 24px',
                    borderRadius: '6px'
                  }}
                >
                  {opt.name}
                </button>
              ))}
            </div>
          </div>
        </div>
      </Card>
    </form>
  );
};

export default SettingsPage;
