//frontend/src/App.jsx
import React, { useState, useEffect } from 'react';
import {
  DashboardIcon,
  InventoryIcon,
  SalesIcon,
  CustomersIcon,
  FinancialIcon,
  ChartBarIcon,
  SettingsIcon,
} from './components/icons';

import InventoryPage from './pages/InventoryPage';
import CustomersPage from './pages/CustomersPage';
import SalesPage from './pages/SalesPage';
import ReceiptPage from './pages/ReceiptPage';
import DashboardPage from './pages/DashboardPage';
import FinancialPage from './pages/FinancialPage';
import ReportsPage from './pages/ReportsPage';
import SettingsPage from './pages/SettingsPage';

import { api } from './api/api'; // API real

const initialView = { page: 'dashboard' };

const NavItem = ({ icon, label, isActive, onClick }) => (
  <li className="mb-2">
    <a
      href="#"
      onClick={(e) => {
        e.preventDefault();
        onClick();
      }}
      className={`flex items-center p-3 rounded-lg transition-colors ${
        isActive
          ? 'bg-primary-600 text-white shadow-md'
          : 'text-primary-100 hover:bg-primary-700 hover:text-white'
      }`}
    >
      {icon}
      <span className="ml-4 font-semibold">{label}</span>
    </a>
  </li>
);

const Sidebar = ({ currentPage, onNavigate }) => (
  <aside className="w-64 bg-primary-900 text-white p-4 flex flex-col flex-shrink-0 no-print">
    <div className="logo text-2xl font-bold text-center py-4 mb-8 border-b border-primary-800">
      EasyStock360
    </div>
    <nav>
      <ul>
        <NavItem icon={<DashboardIcon />} label="Dashboard" isActive={currentPage === 'dashboard'} onClick={() => onNavigate('dashboard')} />
        <NavItem icon={<InventoryIcon />} label="Estoque" isActive={currentPage === 'inventory'} onClick={() => onNavigate('inventory')} />
        <NavItem icon={<SalesIcon />} label="Vendas" isActive={currentPage === 'sales'} onClick={() => onNavigate('sales')} />
        <NavItem icon={<CustomersIcon />} label="Clientes" isActive={currentPage === 'customers'} onClick={() => onNavigate('customers')} />
        <NavItem icon={<FinancialIcon />} label="Financeiro" isActive={currentPage === 'financial'} onClick={() => onNavigate('financial')} />
        <NavItem icon={<ChartBarIcon />} label="Relatórios" isActive={currentPage === 'reports'} onClick={() => onNavigate('reports')} />
      </ul>
    </nav>
    <div className="mt-auto text-primary-200">
      <div className="border-t border-primary-800 my-4"></div>
      <ul>
        <NavItem icon={<SettingsIcon />} label="Configurações" isActive={currentPage === 'settings'} onClick={() => onNavigate('settings')} />
      </ul>
      <div className="text-center text-xs pt-4">
        <p>&copy; 2024 Easydata360</p>
        <p>MVP Version</p>
      </div>
    </div>
  </aside>
);

const App = () => {
  const [currentView, setCurrentView] = useState(initialView);
  const [companySettings, setCompanySettings] = useState(null);

  const handleNavigate = (view) => setCurrentView(view);
  const handleSimpleNavigate = (page) => setCurrentView({ page });

  // Carrega configurações da empresa ao iniciar
  useEffect(() => {
    const loadSettings = async () => {
      try {
        const res = await api.get('/settings');
        setCompanySettings(res.data);
      } catch (err) {
        console.warn('Não foi possível carregar configurações da empresa:', err);
      }
    };
    loadSettings();
  }, []);

  // Aplica tema (cor primária e fonte)
  useEffect(() => {
    if (!companySettings) return;
    const colorMap = {
      blue: '#0066cc',
      green: '#00aa55',
      red: '#cc0033',
    };

    const mainColor = colorMap[companySettings.themeColor] || '#0066cc';
    const fontSize =
      companySettings.fontSize === 'small'
        ? '14px'
        : companySettings.fontSize === 'large'
        ? '18px'
        : '16px';

    document.documentElement.style.setProperty('--main-color', mainColor);
    document.body.style.fontSize = fontSize;
  }, [companySettings]);

  const renderContent = () => {
    switch (currentView.page) {
      case 'dashboard':
        return (
          <DashboardPage
            onNavigate={handleNavigate}
            onNavigateToReceipt={(id) => handleNavigate({ page: 'receipt', transactionId: id })}
          />
        );
      case 'inventory':
        return <InventoryPage filters={currentView.filters} />;
      case 'sales':
        return <SalesPage onNavigateToReceipt={(id) => handleNavigate({ page: 'receipt', transactionId: id })} />;
      case 'customers':
        return <CustomersPage />;
      case 'financial':
        return <FinancialPage initialTab={currentView.tab} />;
      case 'reports':
        return <ReportsPage />;
      case 'settings':
        return <SettingsPage />;
      case 'receipt':
        return <ReceiptPage transactionId={currentView.transactionId} onBack={() => handleNavigate({ page: 'sales' })} />;
      default:
        return <DashboardPage />;
    }
  };

  return (
    <div className="flex h-screen bg-base-100 font-sans">
      <Sidebar currentPage={currentView.page} onNavigate={handleSimpleNavigate} />
      <main className="flex-1 p-8 overflow-y-auto">{renderContent()}</main>
    </div>
  );
};

export default App;
